import React from "react";

export default class Header extends React.Component {
  constructor(props) {
    super(props);
  }

  handleClick(e) {
    e.preventDefault();
    this.props.handleDeleteItems();
  }

  render() {
    const { number, checkedItemsFlag } = this.props;
    const showLinkClass = checkedItemsFlag ? "is--visible" : "is--hidden";
    return (
      <header>
        <h1 className="clearfix">
          {"React Todo List üê∂"}
          <a
            className={`float--right text--xs spacer--top--xs text--reg  ${showLinkClass}`}
            href="#"
            onClick={e => this.handleClick(e)}
          >
            Clear Completed Items ({number})
          </a>
        </h1>
      </header>
    );
  }
}
